maze_nodes = {}

def convert(maze,size):
    t = np.zeros((size,size),dtype = np.int)
    four_maze = np.zeros((size,size,4),dtype = np.int)
    for x in range(size):
        for y in range(size):
            four_maze[x][y][0]=maze[x][y][0]
            four_maze[x][y][1]=maze[x][y][1]
            four_maze[x][y][2]=maze[x][y+1][0]
            four_maze[x][y][3]=maze[x+1][y][1]  
            t[x][y] = four_maze[x][y][0]*1000+four_maze[x][y][1]*100+four_maze[x][y][2]*10+four_maze[x][y][3]
    return four_maze
